function getAll(t){return Array.prototype.slice.call(document.querySelectorAll(t),0)}function decode_email(t,e){for(shift=e.length,link="",i=0;i<e.length;i++)-1==t.indexOf(e.charAt(i))?(ltr=e.charAt(i),link+=ltr):(ltr=(t.indexOf(e.charAt(i))-shift+t.length)%t.length,link+=t.charAt(ltr));return link}document.addEventListener("DOMContentLoaded",function(){var t=getAll("meta[name=contact]"),e=t[0].getAttribute("data-key"),n=t[0].getAttribute("data-encoded");getAll(".has-secret-action").forEach(function(t){t.setAttribute("action","https://formspree.io/"+decode_email(e,n))});var i=getAll(".navbar-burger");i.length>0&&i.forEach(function(t){t.addEventListener("click",function(){var e=t.dataset.target,n=document.getElementById(e);t.classList.toggle("is-active"),n.classList.toggle("is-active")})})});